import React, { Component } from 'react';


export default class Formulario extends Component {

    chkboxSwitch = () => {
        var estado = document.getElementById('terms').value;
        if (estado == 'Acepto') {
            estado.setState="No Acepto";
        } else {
            estado.html="Acepto";
        }
    }

    //Se generan los enlaces para poder tener acceso a los datos que vamos a recoger
    emailRef = React.createRef();
    dataset1Ref = React.createRef();
    dataset2Ref = React.createRef();
    termsRef = React.createRef();

    recibirFormulario = (e) => {
        //Evita que se recargue la página y se envíe el formulario
        e.preventDefault();
        var email = this.emailRef.current.value;
        var dts1 = this.dataset1Ref.current.value;
        var dts2 = this.dataset2Ref.current.value;
        var terms = this.termsRef.current.value;

        if (terms === 'Acepto') {
            alert("todo correcto");
        } else {
            alert('cagamo');
        }

    }

    render() {
        return (
            <form onSubmit={this.recibirFormulario}>
                <div className='form-group'>
                    <label htmlFor='email' >Email*: </label>
                    <input type='email' id='email' name='email' required ref={this.emailRef} />
                </div>
                <div className='form-group'>
                    <label htmlFor='dataset1'>Dataset de entrenamiento*: </label>
                    <input type='file' id='dataset1' name='dataset1' required ref={this.dataset1Ref} />
                </div>
                <div className='form-group'>
                    <label>Dataset para clasificar: </label>
                    <input type='file' id='dataset2' name='dataset2' ref={this.dataset2Ref} />
                </div>
                <div className='form-group'>
                    <label><input type="checkbox" name="terms" id="terms" value="Acepto" onChange="chkboxSwitch()" ref={this.termsRef}  defaultChecked={true} /> Acepto los términos y condiciones del servicio.</label><br />
                </div>
                <div className='form-group'>
                    <input type='submit' value="Entrenar" />
                </div>



            </form>
        );
    }
}
