import React, { Component } from 'react';

export default class Formulario extends Component {

    //Se generan los enlaces para poder tener acceso a los datos que vamos a recoger
    emailRef = React.createRef();
    dataset1Ref = React.createRef();
    dataset2Ref = React.createRef();
    terms = React.createRef();

    recibirFormulario = (e) => {
        //Evita que se recargue la página y se envíe el formulario
        e.preventDefault();
        alert(this.emailRef.current.value);
    }

    render() {
        return (
            <form onSubmit={this.recibirFormulario}>
                <label>Email*: <input type='email' id='email' name='email' required ref={this.emailRef}/></label><br />
                <label>Dataset de entrenamiento*: <input type='file' id='dataset1' name='dataset1'  ref={this.dataset1Ref}/></label><br />
                <label>Dataset para clasificar: <input type='file' id='dataset2' name='dataset2' ref={this.dataset2Ref}/></label><br /><br />
                <label><input type="checkbox" name="terms" id="terms" value='I Agree Terms and Coditions' required /> Acepto los términos y condiciones del servicio.</label><br />
                <input type='submit' value="Entrenar" />
            </form>
        );
    }
}
